import React, { useState, useEffect } from "react";
import { useGithubUser } from "./useGitHubUser";

function GithubUser(props) {
    const { userData, loading, error, fetchUserData } = useGithubUser(props.username);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error.message}</div>;
    }

    if (!userData) {
        return null;
    }

    return (
        <div>
            <p>Username: {userData.login}</p>
            <p>Number of public repositories: {userData.public_repos}</p>
            <p>Followers: {userData.followers}</p>
            <p>Following: {userData.following}</p>
            <p>Profile URL: <a href={userData.html_url}>{userData.html_url}</a></p>
            <img src={userData.avatar_url} alt="User avatar" />
            <button onClick={fetchUserData}>Refresh</button>
        </div>
    );
}

export default GithubUser;
