import React, { useState, useMemo } from "react";

const FilteredList = ({ list }) => {
    const [ageFilter, setAgeFilter] = useState(18);

    const filteredList = useMemo(() => {
        return list.filter((item) => item.age > ageFilter);
    }, [list, ageFilter]);

    const handleAgeFilterChange = (e) => {
        setAgeFilter(parseInt(e.target.value));
    };

    return (
        <div>
            <h2>FilteredList</h2>
            <div>
                Age Filter:{" "}
                <input type="number" value={ageFilter} onChange={handleAgeFilterChange} />
            </div>
            <ul>
                {filteredList.map((item) => (
                    <li key={item.id}>
                        {item.name} ({item.age})
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default FilteredList;
